import React, { useState } from 'react';
import UploadVideo from './components/UploadVideo';  // Adjusted import path
import LiveFeed from './components/LiveFeed';  // Adjusted import path

const HomePage = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleSelectOption = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="home-page flex flex-col items-center justify-center min-h-screen">
      <h1 className="text-4xl font-bold text-green-600 mb-8">CCTV Detection System</h1>
      
      {!selectedOption && (
        <div className="options flex flex-col items-center">
          <button
            onClick={() => handleSelectOption('upload')}
            className="px-4 py-2 bg-blue-500 text-white rounded mb-4"
          >
            Upload Video
          </button>
          <button
            onClick={() => handleSelectOption('live')}
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Live Detection
          </button>
        </div>
      )}

      {selectedOption === 'upload' && (
        <UploadVideo />
      )}

      {selectedOption === 'live' && (
        <LiveFeed />
      )}
    </div>
  );
};

export default HomePage;
