import React, { useState } from 'react';

const UploadVideo = () => {
  const [videoFile, setVideoFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState(null);

  // Handle file selection
  const handleFileChange = (e) => {
    setVideoFile(e.target.files[0]);
  };

  // Handle video upload
  const handleUpload = async (e) => {
    e.preventDefault();

    if (!videoFile) {
      setUploadStatus("Please select a video file.");
      return;
    }

    const formData = new FormData();
    formData.append('file', videoFile);

    try {
      // Send video to backend
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        setUploadStatus(`Video uploaded and processed. Cell phone detected: ${data.cell_phone_detected}`);
      } else {
        setUploadStatus(data.message || "Failed to upload video");
      }
    } catch (error) {
      setUploadStatus("Error uploading video");
    }
  };

  return (
    <div className="flex flex-col items-center">
      <input
        type="file"
        accept="video/*"
        onChange={handleFileChange}
        className="mb-4 p-2 border rounded"
      />
      <button
        onClick={handleUpload}
        className="px-6 py-3 bg-blue-500 text-white rounded-lg"
      >
        Upload Video
      </button>
      {uploadStatus && (
        <div className="mt-4 text-lg">{uploadStatus}</div>
      )}
    </div>
  );
};

export default UploadVideo;
