import React, { useState, useEffect } from "react";
import axios from "axios";

const LiveFeed = () => {
  const [cellPhoneDetected, setCellPhoneDetected] = useState(false);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:5000/cell_phone_status");
        setCellPhoneDetected(response.data.cell_phone_detected);
      } catch (error) {
        console.error("Error fetching status:", error);
      }
    };

    const interval = setInterval(checkStatus, 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="text-center p-6">
      <h1 className="text-3xl font-bold text-blue-600">Live Object Detection</h1>
      <h2 className={`text-xl ${cellPhoneDetected ? "text-red-600" : "text-green-600"}`}>
        {cellPhoneDetected ? "ðŸ“± Cell Phone Detected!" : "No Cell Phone Detected"}
      </h2>
      <img src="http://127.0.0.1:5000/video_feed" className="border rounded-lg shadow-lg mx-auto mt-5" alt="Live Feed" />
    </div>
  );
};

export default LiveFeed;
